mapper.select:
select es.service_name,es.pre_vip_price_descript,es.original_price,es.vip_price,esd.description,es.unit, es.background_picture,es.id,es.scence_key,es.begin_time,es.end_time,es.recommend_yn,es.create_time from elegant_service es left join elegant_service_description esd on es.id = esd.elegant_service_id right join elegant_service_identity_type esit on esit.elegant_service_id = es.id left join meta_identity_type mit on esit.meta_identity_type_id = mit.id right join elegant_service_service_type esst on esst.elegant_service_id = es.id left join meta_service_type mst on esst.meta_service_type_id = mst.id INNER JOIN elegant_service_approve_type esat on esat.elegant_service_id = es.id INNER JOIN elegant_service_member_type esmt on esmt.elegant_service_id = es.id where es.on_off = 1 and es.yn = 1
and es.appid = #{appid}
and esit.meta_identity_type_id in #{item}
and esst.meta_service_type_id in #{item}
and ( es.service_name like CONCAT(CONCAT('%', #{searchWord}), '%') or mit.type_name like CONCAT(CONCAT('%', #{searchWord}), '%') or mst.service_type_name like CONCAT(CONCAT('%', #{searchWord}), '%') or esd.description like CONCAT(CONCAT('%', #{searchWord}), '%'))
and es.recommend_yn = 1
and esat.approve_type = #{approveType}
and es.is_lead_investor = #{isLeadInvestor}
and es.is_reward = #{isReward}
and esmt.member_type_id = #{memberType}
group by es.id
order by es.create_time desc
order by es.sort
limit #{startPage},#{pageSize}

select * from(
select es.*,esc.cooperation_name from elegant_service es
left join elegant_service_cooperation esc on es.id = esc.elegant_service_id
left join elegant_service_identity_type esit on esit.elegant_service_id = es.id
left join meta_identity_type mit on esit.meta_identity_type_id = mit.id
left join elegant_service_service_type esst on esit.elegant_service_id = esst.elegant_service_id
left join meta_service_type mst on esit.meta_identity_type_id = mst.id
INNER JOIN elegant_service_approve_type esat on esat.elegant_service_id = es.id
INNER JOIN elegant_service_member_type esmt on esmt.elegant_service_id = es.id
where es.yn = 1

 and es.appid = #{appid}


  and  date(es.begin_time)between

  #{beginTime} and #{endTime}



  and esat.approve_type = #{approveType}



  and es.is_lead_investor = #{isLeadInvestor}



  and es.is_reward = #{isReward}



  and esmt.member_type_id = #{memberType}



  and (mit.type_name like  CONCAT(CONCAT('%', #{searchWord}), '%')
  or es.service_name like  CONCAT(CONCAT('%', #{searchWord}), '%')
  or mst.service_type_name like  CONCAT(CONCAT('%', #{searchWord}), '%')
  or esc.cooperation_name like  CONCAT(CONCAT('%', #{searchWord}), '%'))



group by es.id
) as a
order by a.sort
limit #{startPage},#{pageSize}

select count(*) from(
select es.*,esc.cooperation_name from elegant_service es
left join elegant_service_cooperation esc on es.id = esc.elegant_service_id
left join elegant_service_identity_type esit on esit.elegant_service_id = es.id
left join meta_identity_type mit on esit.meta_identity_type_id = mit.id
left join elegant_service_service_type esst on esit.elegant_service_id = esst.elegant_service_id
left join meta_service_type mst on esit.meta_identity_type_id = mst.id
INNER JOIN elegant_service_approve_type esat on esat.elegant_service_id = es.id
INNER JOIN elegant_service_member_type esmt on esmt.elegant_service_id = es.id
where es.yn = 1

  and es.appid = #{appid}


  and  date(es.begin_time)between

  #{beginTime} and #{endTime}


  and esat.approve_type = #{approveType}



  and es.is_lead_investor = #{isLeadInvestor}



  and es.is_reward = #{isReward}



  and esmt.member_type_id = #{memberType}


  and (mit.type_name like  CONCAT(CONCAT('%', #{searchWord}), '%')
  or es.service_name like  CONCAT(CONCAT('%', #{searchWord}), '%')
  or mst.service_type_name like  CONCAT(CONCAT('%', #{searchWord}), '%')
  or esc.cooperation_name like  CONCAT(CONCAT('%', #{searchWord}), '%'))

group by es.id
) as a
