mapper.select:
select * from investment_institutions where 1=1
and short_name like concat(concat("%",#{shortName}),"%")
GROUP BY `short_name` order by sort desc limit #{startPage},#{pageSize}

SELECT ii.id, ii.short_name,ii.create_time,ii.type,ii.logo,ii.kenel_case,ii.count,ii.sort FROM
investment_institutions ii
WHERE  1=1

    AND ii.type IN
    #{item}


    AND ii.id IN
    #{item}

GROUP BY ii.short_name
ORDER BY ii.count desc
LIMIT #{beginNum},#{pageSize}

SELECT investment_institution_id FROM  project_financing_investment_institution_relationship WHERE
project_send_log_id IN
#{item}

select ii.id, ii.short_name,ii.create_time,ii.type,ii.logo,ii.kenel_case,ii.comment,d.cont count from (SELECT c.short_name,SUM(c.cc) cont from ( SELECT a.short_name ,count(DISTINCT(p_name)) cc FROM (
select case when p.full_name is null or p.full_name = '' then p.short_name else p.full_name end p_name, pf.`stage` , p.`city` , ii.`short_name` from project_financing_log pf left join projects p on pf.project_id = p.id LEFT join investment_institutions_project iip on iip.project_id = pf.id left join investment_institutions ii on iip.investment_institutions_id = ii.id where pf.financing_time between #{beginTime} and #{endTime} and trim(pf.stage) in ('种子轮','天使轮','Pre-A轮') and p.`country` = '中国' AND ii.`short_name` IS NOT NULL AND ii.`short_name` NOT LIKE '%个人%' and ii.`short_name` not in ('自投','自筹') and ii.`short_name` NOT LIKE '%天使投资人%' GROUP BY p_name , ii.`short_name` ) a GROUP BY a.short_name ) c GROUP BY c.short_name ORDER BY SUM(c.cc) DESC ) d LEFT JOIN `investment_institutions` ii on d.short_name = ii.`short_name` where ii.`logo` IS NOT NULL
LIMIT #{beginNum},#{pageSize}

SELECT ii.id, ii.short_name,ii.create_time,ii.type ,case when ii.logo is null or ii.logo = "" then "http://img.idatavc.com/static/logo/jg_default.png" else ii.logo end logo, case when ii.kenel_case is null then (case when ii.`comment` is null then "" else ii.`comment` end ) else ii.kenel_case end kenel_case, case when ii.count is null then 0 else ii.count end count,ii.sort FROM investment_institutions ii WHERE 1=1 and ii.short_name not like "%个人%" and ii.short_name not like "%投资人%" and ii.short_name not like "%自投%" and ii.short_name not like "%自筹%" and ii.short_name not like "%以及%" and ii.short_name not like "%创始人%" AND ii.short_name != ""
AND ii.id IN #{item}
GROUP BY ii.short_name ORDER BY ii.sort

SELECT ii.id, ii.short_name,ii.create_time,ii.type,ii.logo,ii.kenel_case,ii.count,ii.sort FROM investment_institutions ii WHERE 1=1 and ii.short_name not like "%个人%" and ii.short_name not like "%投资人%" and ii.short_name not like "%自投%" and ii.short_name not like "%自筹%" and ii.short_name not like "%以及%" and ii.short_name not like "%创始人%" AND ii.short_name != ""
AND ii.short_name IN #{item}
AND ii.logo IS NOT NULL AND ii.logo != '' AND ii.count IS NOT NULL GROUP BY ii.short_name ORDER BY ii.sort

SELECT ii.* FROM investment_institutions ii
left join investment_institutions_segmentation iis on ii.ID = iis.investment_institutions_id left join meta_segmentation ms on iis.meta_segmentation_id = ms.id
left join investment_institutions_stage iist on ii.id = iist.investment_institution_id left join meta_project_stage mps on iist.meta_project_stage_id = mps.id
where 1=1 and ii.short_name not like "%个人%" and ii.short_name not like "%投资人%" and ii.short_name not like "%自投%" and ii.short_name not like "%自筹%" and ii.short_name not like "%以及%" and ii.short_name not like "%创始人%" AND ii.short_name != ""
and ms.`name` in #{item}
and mps.`name` in #{item}
and ii.types = #{types}
group by ii.short_name order by ii.count desc limit #{startPage},#{pageSize}

SELECT ii.id FROM project_financing_investment_institution_relationship pfiir
left join project_send_logs psl on pfiir.project_send_log_id = psl.id
left join investment_institutions ii on pfiir.investment_institution_id = ii.id
where userid = #{userId}
group by short_name
order by ii.id;
