mapper.select:
select * from investment_institutions where 1=1
and short_name like concat(concat("%",#{shortName}),"%")
GROUP BY `short_name` order by sort desc limit #{startPage},#{pageSize}

SELECT COUNT(1) count,c.short_name,c.create_time,c.type,c.logo, case when c.kenel_case is null then (case when c.comment is null then "" else c.comment end) else c.kenel_case end kenel_case, c.comment,c.id FROM (SELECT ii.id,ii.short_name,ii.create_time,ii.type,ii.logo,ii.kenel_case,ii.comment, CASE WHEN p.full_name IS NULL OR p.full_name = '' THEN p.short_name ELSE p.full_name END p_name, pf.financing_time FROM project_financing_log pf LEFT JOIN projects p ON pf.project_id = p.id LEFT JOIN investment_institutions_project iip ON iip.project_id = pf.id LEFT JOIN investment_institutions ii ON iip.investment_institutions_id = ii.id WHERE pf.financing_time BETWEEN '2017-01-01' AND '2017-12-31' AND TRIM(pf.stage) IN ('种子轮' , '天使轮', 'Pre-A轮') AND p.`country` = '中国'
and (ii.short_name like CONCAT("%",CONCAT(#{shortName},"%")) or ii.kenel_case like CONCAT("%",CONCAT(#{shortName},"%")) or ii.comment like CONCAT("%",CONCAT(#{shortName},"%")))
GROUP BY p_name , pf.stage) c WHERE c.short_name != '' AND c.short_name IS NOT NULL and c.short_name not like "%个人%" and c.short_name not like "%投资人%" and c.short_name not like "%自投%" and c.short_name not like "%自筹%" and c.short_name not like "%以及%" and c.short_name not like "%创始人%" GROUP BY c.short_name ORDER BY count DESC LIMIT #{beginNum},#{pageSize}

