mapper.select:
SELECT b.id,b.user_name,b.userid, case when u.headpic_real is null then (case when u.headpic is null then "" else u.headpic end) else u.headpic_real end headpic, b.company_name,b.company_duties,b.phonenumber, ids.segmentation,idsw.speedway,a.stage,b.investment_amount_low,b.investment_amount_high, b.investment_amount_low_dollars,b.investment_amount_high_dollars,idc.user_character, b.future,b.demand_status,b.update_time FROM investor_demand b left join users u on b.userid = u.id left join (SELECT investor_demand_id,group_concat(`character`) as user_character FROM investor_demand_character group by investor_demand_id) idc on b.id = idc.investor_demand_id left join (SELECT investor_demand_id,group_concat(segmentation) segmentation FROM investor_demand_segmentation group by investor_demand_id) ids on b.id = ids.investor_demand_id left join (SELECT investor_demand_id,group_concat(speedway) speedway FROM investor_demand_speedway group by investor_demand_id) idsw on b.id = idsw.investor_demand_id left join (SELECT investor_demand_id,group_concat(mps.`name`) stage FROM investor_demand_stage idse left join meta_project_stage mps on idse.meta_project_stage_id = mps.id group by investor_demand_id) a on b.id = a.investor_demand_id where 1=1
and b.demand_status in #{item}
and b.demand_status != 3
and b.appid = #{appid}
order by b.creat_time desc limit #{startPage},#{pageSize}

Select count(*) ct from( SELECT b.id,b.user_name,b.userid, case when u.headpic is null then (case when u.headpic_real is null then "" else u.headpic_real end) else u.headpic end headpic, b.company_name,b.company_duties,b.phonenumber, ids.segmentation,idsw.speedway,a.stage,b.investment_amount_low,b.investment_amount_high, b.investment_amount_low_dollars,b.investment_amount_high_dollars,idc.user_character, b.future,b.demand_status,b.update_time FROM investor_demand b left join users u on b.userid = u.id left join (SELECT investor_demand_id,group_concat(`character`) as user_character FROM investor_demand_character group by investor_demand_id) idc on b.id = idc.investor_demand_id left join (SELECT investor_demand_id,group_concat(segmentation) segmentation FROM investor_demand_segmentation group by investor_demand_id) ids on b.id = ids.investor_demand_id left join (SELECT investor_demand_id,group_concat(speedway) speedway FROM investor_demand_speedway group by investor_demand_id) idsw on b.id = idsw.investor_demand_id left join (SELECT investor_demand_id,group_concat(mps.`name`) stage FROM investor_demand_stage idse left join meta_project_stage mps on idse.meta_project_stage_id = mps.id group by investor_demand_id) a on b.id = a.investor_demand_id where 1=1
and b.demand_status in #{item}
and b.demand_status != 3
and b.appid = #{appid}
order by b.creat_time desc ) abc
