mapper.select:
SELECT ia.id, case when ivs.investors_type is null then "" else (case when ivs.investors_type = 0 then "个人投资人" else (case when ivs.investors_type = 1 then "机构投资人" else "VIP投资人" end) end) end shenfen, ia.userid,u.company_name company,ia.company_duties,u.phonenumber,u.actual_name approval_username, case when ia.investors_type is null then "" else (case when ia.investors_type = 0 then "个人投资人" else (case when ia.investors_type = 1 then "机构投资人" else "VIP投资人" end) end) end investors_type, ia.description,ia.work_card,ia.investors_approvalcol_case,ia.create_time,ia.approval_result FROM investors_approval ia left join users u on ia.userid = u.ID left join users_weixin uw on ia.userid = uw.user_id left join (select * from investors where approval_status = 1) ivs on ia.userid = ivs.user_id where 1=1
and (ia.approval_username like CONCAT("%",CONCAT(#{searchWord},"%")) or ia.company like CONCAT("%",CONCAT(#{searchWord},"%")) or ia.company_duties like CONCAT("%",CONCAT(#{searchWord},"%")) or u.phonenumber like CONCAT("%",CONCAT(#{searchWord},"%")))
and ia.investors_type in #{item}
and ia.approval_result in #{item}
order by ia.create_time
desc
limit #{startPage},#{pageSize};

select count(*) ct from ( SELECT ia.id, case when ivs.investors_type is null then "" else (case when ivs.investors_type = 0 then "个人投资人" else (case when ivs.investors_type = 1 then "机构投资人" else "VIP投资人" end) end) end shenfen, ia.userid,u.company_name company,ia.company_duties,u.phonenumber,ia.approval_username, case when ia.investors_type is null then "" else (case when ia.investors_type = 0 then "个人投资人" else (case when ia.investors_type = 1 then "机构投资人" else "VIP投资人" end) end) end investors_type, ia.description,ia.work_card,ia.investors_approvalcol_case,ia.create_time,ia.approval_result FROM investors_approval ia left join users u on ia.userid = u.ID left join users_weixin uw on ia.userid = uw.user_id left join (select * from investors where approval_status = 1) ivs on ia.userid = ivs.user_id where 1=1
and (ia.approval_username like CONCAT("%",CONCAT(#{searchWord},"%")) or ia.company like CONCAT("%",CONCAT(#{searchWord},"%")) or ia.company_duties like CONCAT("%",CONCAT(#{searchWord},"%")) or u.phonenumber like CONCAT("%",CONCAT(#{searchWord},"%")))
and ia.investors_type in #{item}
and ia.approval_result in #{item}
order by ia.create_time
desc
