mapper.select:
SELECT COUNT(1) total,AVG(mf.money) avgMoney FROM meta_financing mf LEFT JOIN meta_financing_education mfe on
mf.id = mfe.financing_id
LEFT JOIN meta_financing_work mfw on mf.id = mfw.financing_id WHERE mf.money != 0

    AND mf.round = #{roundName}


    AND mf.main_Industry = #{industryName}


    AND mf.register_area = #{cityName}


    AND mfe.education_experience = #{educationName}


    AND mfw.work_experience = #{workName}

SELECT COUNT(1) total,AVG(mf.money) avgMoney FROM meta_financing mf LEFT JOIN meta_financing_education mfe on
mf.id = mfe.financing_id
LEFT JOIN meta_financing_work mfw on mf.id = mfw.financing_id WHERE mf.valuation != 0

    AND mf.round = #{roundName}


    AND mf.main_Industry = #{industryName}


    AND mf.register_area = #{cityName}


    AND mfe.education_experience = #{educationName}


    AND mfw.work_experience = #{workName}

SELECT dis money, COUNT(dis) dcount FROM (
SELECT CEILING(mf.Valuation/500) * 500 as dis, mf.Valuation FROM meta_financing mf LEFT JOIN
meta_financing_education mfe on mf.id = mfe.financing_id
LEFT JOIN meta_financing_work mfw on mf.id = mfw.financing_id WHERE mf.valuation != 0

    AND mf.round = #{roundName}


    AND mf.main_Industry = #{industryName}


    AND mf.register_area = #{cityName}


    AND mfe.education_experience = #{educationName}


    AND mfw.work_experience = #{workName}

) a GROUP BY dis

SELECT dis money, COUNT(dis) dcount FROM (
SELECT CEILING(mf.money/500) * 500 as dis, mf.money FROM meta_financing mf LEFT JOIN meta_financing_education
mfe on mf.id = mfe.financing_id
