mapper.select:
select count(a.full_name) total, AVG (a.amount) avgMoney from (
select full_name,sum(amount) amount,max(financing_time) financing_time from view_projects_info where amount > 0

     AND stage = #{roundName}


    and segmentation = #{industryName}


    and city =#{cityName}


    and education_experience =#{educationName}


    and work_experience =#{workName}

group by full_name ) a
where a.financing_time between #{beginTime} and #{endTime}

select count(a.full_name) total, AVG (a.valuation) avgMoney from (
select full_name,sum(valuation) valuation,max(financing_time) financing_time from view_projects_info where valuation > 0

    and stage = #{roundName}


    and segmentation = #{industryName}


    and city =#{cityName}


    and education_experience =#{educationName}


    and work_experience =#{workName}

group by full_name ) a
where a.financing_time between #{beginTime} and #{endTime}

SELECT dis money, cc dcount from (
select CEILING(a.valuation/5000) * 5000 as dis, count(1) cc from (
select full_name,sum(valuation) valuation,max(financing_time) financing_time from view_projects_info where valuation > 0

and stage = #{roundName}


and segmentation = #{industryName}


and city =#{cityName}


and education_experience =#{educationName}


and work_experience =#{workName}

group by full_name ) a
where a.financing_time between #{beginTime} and #{endTime}
group by CEILING(a.valuation/5000) * 5000

) b order by b.cc desc
limit #{index}, #{size}

SELECT dis money, cc dcount from (
select CEILING(a.amount/500) * 500 as dis, count(1) cc from (
select full_name,sum(amount) amount,max(financing_time) financing_time from view_projects_info where amount > 0

    and stage = #{roundName}


    and segmentation = #{industryName}


    and city =#{cityName}


    and education_experience =#{educationName}


    and work_experience =#{workName}

group by full_name ) a
where a.financing_time between #{beginTime} and #{endTime}
group by CEILING(a.amount/500) * 500
) b order by b.cc desc
limit #{index}, #{size}
