mapper.select:
    SELECT pfl.ID,pfl.serial_number,mdst.type_name,ii.id as investment_institution_id,ii.short_name as institution_name,
    p.id as project_id,p.short_name as project_name,ps.segmentation_name,pfl.stage,pfl.amount,pfl.currency,
    pfl.share_divest,pfl.valuation,pfl.total_amount,pfl.pr_amount,pfl.financing_time,pfl.Investment_institutions_list,
    pfl.proportion_list,pfl.create_time,pfl.update_time,pfl.approval_status,pfl.approval_time,pfl.submitter,u.actual_name
    FROM project_financing_log pfl
    left join meta_data_source_type mdst on pfl.data_soruce_type_id = mdst.id
    left join investment_institutions_project iip on pfl.id = iip.project_id
    left join investment_institutions ii on iip.investment_institutions_id = ii.id
    left join projects p on pfl.project_id = p.id
    left join
    (select project_id,group_concat(segmentation_name) segmentation_name from project_segmentation group by project_id) ps
    on p.id = ps.project_id
    
    left join user_token ut on pfl.submitter = ut.token
    left join users u on ut.user_id = u.ID
    where 1=1


      and
        (ii.short_name like CONCAT("%",CONCAT(#{searchWord},"%"))
      or p.short_name like CONCAT("%",CONCAT(#{searchWord},"%"))
      or ps.segmentation_name like CONCAT("%",CONCAT(#{searchWord},"%"))
      or pfl.stage like CONCAT("%",CONCAT(#{searchWord},"%"))
      or pfl.Investment_institutions_list like CONCAT("%",CONCAT(#{searchWord},"%"))
      or pfl.proportion_list like CONCAT("%",CONCAT(#{searchWord},"%")))


     AND (pfl.create_time >= #{beginTime} 
     or pfl.update_time >= #{beginTime}
     )




     AND (pfl.create_time <= #{endTime}
     or pfl.update_time <= #{beginTime}
     )


      and pfl.stage in 
	#{item}


      and pfl.currency in 
      #{item}


      and pfl.data_soruce_type_id in  
      #{item}


      order by pfl.${column} ${order}

   LIMIT #{start},#{pageSize}

select count(*) FROM project_financing_log pfl left join meta_data_source_type mdst on pfl.data_soruce_type_id = mdst.id left join investment_institutions_project iip on pfl.id = iip.project_id left join investment_institutions ii on iip.investment_institutions_id = ii.id left join projects p on pfl.project_id = p.id left join (select project_id,group_concat(segmentation_name) segmentation_name from project_segmentation group by project_id) ps on p.id = ps.project_id
left join user_token ut on pfl.submitter = ut.token left join users u on ut.user_id = u.ID where 1=1
and (ii.short_name like CONCAT("%",CONCAT(#{searchWord},"%")) or p.short_name like CONCAT("%",CONCAT(#{searchWord},"%")) or ps.segmentation_name like CONCAT("%",CONCAT(#{searchWord},"%")) or pfl.stage like CONCAT("%",CONCAT(#{searchWord},"%")) or pfl.Investment_institutions_list like CONCAT("%",CONCAT(#{searchWord},"%")) or pfl.proportion_list like CONCAT("%",CONCAT(#{searchWord},"%")) )
AND (pfl.create_time >= #{beginTime} or pfl.update_time >= #{beginTime} )
AND (pfl.create_time <= #{endTime} or pfl.update_time <= #{beginTime} )
and pfl.stage in #{item}
and pfl.currency in #{item}
and pfl.data_soruce_type_id in #{item}

select * from project_financing_log where project_id = #{projectId} and `status`= #{projectStatus}
group by stage
order by financing_time desc
