mapper.select:
SELECT * FROM project_send_audit_b
where user_id = #{userid}
and appid = #{appid}
and audit_status = 0
order by create_time desc
limit 0,1;

SELECT psab.id,u.ID as uid,u.actual_name,psb.short_name,psb.create_time,psab.audit_status,psab.prepare_id FROM project_send_audit_b psab left join users u on psab.user_id = u.ID left join project_send_b psb on psab.project_send_b_id = psb.id where psab.appid = #{appid}
and psb.short_name like CONCAT('%',#{shortName},'%')
order by psab.create_time desc limit #{startPage},#{pageSize}

SELECT psab.id,psab.project_source,psb.short_name,psb.full_name,psb.kernel_desc, psb.city,u.actual_name,u.company_name,u.company_duties,u.phonenumber,aprl.rating_stage,psb.create_time,psab.audit_status,psab.audit_time, ps.segmentation_name FROM project_send_audit_b psab left join project_send_b psb on psab.project_send_b_id = psb.id left join users u on psab.user_id = u.ID left join projects p on psb.short_name =p.short_name left join admin_project_rating_log aprl on p.id = aprl.project_id left join (SELECT project_send_b_id,group_concat(segmentation_name) as segmentation_name FROM idatavc.project_send_segmentation_b group by project_send_b_id) ps on psb.id = ps.project_send_b_id where 1=1
and (ps.segmentation_name like CONCAT("%",CONCAT(#{searchWord},"%")) or psb.short_name like CONCAT("%",CONCAT(#{searchWord},"%")) or psb.full_name like CONCAT("%",CONCAT(#{searchWord},"%")) or psb.kernel_desc like CONCAT("%",CONCAT(#{searchWord},"%")) or u.actual_name like CONCAT("%",CONCAT(#{searchWord},"%")))
and psb.create_time between #{begainTime} and #{endTime}
and psab.project_source =#{projetcSource}
order by psab.create_time
desc
order by psab.audit_time
desc
limit #{startPage},#{pageSize}

select count(*) ct from( SELECT psab.id,psab.project_source,psb.short_name,psb.full_name,psb.kernel_desc, psb.city,u.actual_name,u.company_name,u.company_duties,u.phonenumber,aprl.rating_stage,psb.create_time,psab.audit_status,psab.audit_time, ps.segmentation_name FROM project_send_audit_b psab left join project_send_b psb on psab.project_send_b_id = psb.id left join users u on psab.user_id = u.ID left join projects p on psb.short_name =p.short_name left join admin_project_rating_log aprl on p.id = aprl.project_id left join (SELECT project_send_b_id,group_concat(segmentation_name) as segmentation_name FROM idatavc.project_send_segmentation_b group by project_send_b_id) ps on psb.id = ps.project_send_b_id where 1=1
and (ps.segmentation_name like CONCAT("%",CONCAT(#{searchWord},"%")) or psb.short_name like CONCAT("%",CONCAT(#{searchWord},"%")) or psb.full_name like CONCAT("%",CONCAT(#{searchWord},"%")) or psb.kernel_desc like CONCAT("%",CONCAT(#{searchWord},"%")) or u.actual_name like CONCAT("%",CONCAT(#{searchWord},"%")))
and psb.create_time between #{begainTime} and #{endTime}
and psab.project_source =#{projetcSource}
order by psab.create_time
desc
order by psab.audit_time
desc
) abcd
