SELECT mf.id ID,mf.short_name,mf.kernel_desc,mf.`city`,mf.`segmentation` segementation,mf.stage
FROM view_segemention_info mf where 1=1

    AND mf.investment_institutions_type IN
    #{item}


    AND mf.segmentation IN
    #{item}



    AND mf.stage IN
    #{item}


GROUP BY mf.id LIMIT #{sizea},#{froma}

select p.id ID,p.short_name,p.kernel_desc,p.city,ps.segmentation_name segementation,pfl.stage,p.evaluation_recommend from projects p left join project_financing_log pfl on p.id = pfl.project_id
right join project_segmentation ps on ps.project_id = p.id
left join   investment_institutions ii on p.investment_institutions_id = ii.id
left join founders f on f.project_id = p.id
right join founders_education fe on fe.founder_ID = f.id
right join founders_work fw on fw.founder_id = f.id
where  p.project_type = 0

    AND ii.type IN
    #{item}



    AND ps.segmentation_name IN
    #{item}




     AND pfl.stage IN
     #{item}




    AND p.city IN
    #{item}



    AND  fw.work_experience IN
    #{item}




    AND fe.education_experience IN
    #{item}


GROUP BY short_name, stage
ORDER BY evaluation_recommend DESC
LIMIT #{sizea},#{froma}

select p.id ID,p.short_name,p.kernel_desc,p.city,ps.segmentation_name segementation,pfl.stage,p.evaluation_recommend from projects p left join project_financing_log pfl on p.id = pfl.project_id right join project_segmentation ps on ps.project_id = p.id left join investment_institutions ii on p.investment_institutions_id = ii.id left join founders f on f.project_id = p.id right join founders_education fe on fe.founder_ID = f.id right join founders_work fw on fw.founder_id = f.id where pfl.financing_time BETWEEN '2017-01-01' and '2017-06-30' and pfl.stage IN('天使轮','种子轮','Pre-A轮') and p.project_type = 0
AND ii.type IN #{item}
AND ps.segmentation_name IN #{item}
AND p.city IN #{item}
AND fw.work_experience IN #{item}
AND fe.education_experience IN #{item}
GROUP BY short_name, stage ORDER BY evaluation_recommend DESC LIMIT #{sizea},#{froma}

SELECT avg(n.amount) FROM projects m left join project_financing_log n on m.ID = n.project_id right join project_segmentation f on m.ID = f.project_id where n.amount > 0 and n.financing_time >= '2016-07-01'

    and f.segmentation_name = #{segmentation}


    and n.stage = #{stage};

SELECT avg(n.amount) FROM projects m left join project_financing_log n on m.ID = n.project_id right join project_segmentation f on m.ID = f.project_id where n.amount > 0 and n.financing_time >= '2016-07-01'

    and n.stage = #{stage}


    and m.city = #{city};

SELECT avg(n.total_amount) FROM projects m left join project_financing_log n on m.ID = n.project_id right join project_segmentation f on m.ID = f.project_id where n.total_amount > 0 and n.financing_time >= '2016-07-01'

    and f.segmentation_name = #{segmentation}


    and n.stage = #{stage};

SELECT avg(n.total_amount) FROM projects m left join project_financing_log n on m.ID = n.project_id right join project_segmentation f on m.ID = f.project_id where n.total_amount > 0 and n.financing_time >= '2016-07-01'

    and n.stage = #{stage}


    and m.city = #{city};
