where 1=1


    AND mf.investment_institutions_type IN
    #{item}


    AND mf.segmentation IN
    #{item}



    AND mf.stage IN
    #{item}


GROUP BY mf.id LIMIT #{sizea},#{froma}

SELECT mf.id ID,mf.short_name,mf.kernel_desc,mf.`city`,mf.`segmentation` segementation,mf.evaluation_recommend,
(SELECT stage FROM project_financing_log WHERE project_id = mf.`id` ORDER BY financing_time DESC LIMIT 1)stage
FROM projects mf
WHERE 1=1
AND mf.id IN(SELECT a.project_id FROM project_financing_log a WHERE a.financing_time > '2017-01-01')

    AND mf.`investment_institutions_id` IN (SELECT id FROM investment_institutions WHERE `type` IN
    #{item}
    )


    AND mf.id IN (SELECT project_id FROM project_segmentation WHERE segmentation_name IN
    #{item}
    )



    AND mf.id IN (SELECT project_id FROM project_financing_log WHERE stage IN
    #{item}
    )



    AND mf.city IN
    #{item}



    AND mf.id IN (SELECT f.project_id FROM founders_work fw LEFT JOIN founders f ON fw.founder_id = f.id WHERE fw.work_experience IN
    #{item}
    )



    AND mf.id IN (SELECT f.project_id FROM founders_education fe LEFT JOIN founders f ON fe.founder_id = f.id WHERE fe.education_experience IN
    #{item}
    )

GROUP BY mf.short_name,stage
ORDER BY mf.evaluation_recommend DESC
LIMIT #{sizea},#{froma}

SELECT mf.id ID ,mf.short_name,mf.kernel_desc,mf.`city`,mf.`segmentation` segementation,mf.evaluation_recommend,
(SELECT stage FROM project_financing_log WHERE project_id = mf.`id` ORDER BY financing_time DESC LIMIT 1) stage
FROM projects mf
WHERE 1=1
AND mf.id IN(SELECT a.project_id FROM project_financing_log a WHERE a.financing_time BETWEEN '2017-01-01' AND '2017-06-30')
AND mf.id IN (SELECT project_id FROM project_financing_log WHERE stage IN('天使轮','种子轮','Pre-A轮'))

    AND mf.`investment_institutions_id` IN (SELECT id FROM investment_institutions WHERE `type` IN
    #{item}
    )


    AND mf.id IN (SELECT project_id FROM project_segmentation WHERE segmentation_name IN
    #{item}
    )





    AND mf.city IN
    #{item}



    AND mf.id IN (SELECT f.project_id FROM founders_work fw LEFT JOIN founders f ON fw.founder_id = f.id WHERE fw.work_experience IN
    #{item}
    )



    AND mf.id IN (SELECT f.project_id FROM founders_education fe LEFT JOIN founders f ON fe.founder_id = f.id WHERE fe.education_experience IN
    #{item}
    )

GROUP BY mf.short_name, stage
ORDER BY mf.evaluation_recommend DESC
LIMIT #{sizea},#{froma}

SELECT avg(n.amount) FROM projects m left join project_financing_log n on m.ID = n.project_id right join project_segmentation f on m.ID = f.project_id where n.amount > 0 and n.financing_time >= '2016-07-01'

    and f.segmentation_name = #{segmentation}


    and n.stage = #{stage};

SELECT avg(n.amount) FROM projects m left join project_financing_log n on m.ID = n.project_id right join project_segmentation f on m.ID = f.project_id where n.amount > 0 and n.financing_time >= '2016-07-01'

    and n.stage = #{stage}


    and m.city = #{city};

SELECT avg(n.total_amount) FROM projects m left join project_financing_log n on m.ID = n.project_id right join project_segmentation f on m.ID = f.project_id where n.total_amount > 0 and n.financing_time >= '2016-07-01'

    and f.segmentation_name = #{segmentation}


    and n.stage = #{stage};

SELECT avg(n.total_amount) FROM projects m left join project_financing_log n on m.ID = n.project_id right join project_segmentation f on m.ID = f.project_id where n.total_amount > 0 and n.financing_time >= '2016-07-01'

    and n.stage = #{stage}


    and m.city = #{city};

SELECT mf.id ID,mf.short_name,mf.kernel_desc,mf.city, ps.segmentation_name segementation ,mf.`project_logo`,
pfl.stage,mf.evaluation_recommend FROM projects mf RIGHT JOIN project_segmentation ps
ON ps.project_id = mf.id LEFT JOIN project_financing_log pfl ON mf.id = pfl.project_id
WHERE 1=1

AND ps.segmentation_name IN
    #{item}


AND mf.city =#{city}

 AND pfl.stage IN 
    #{item}

and mf.short_name !=#{shortName}
GROUP BY mf.short_name, pfl.stage
ORDER BY mf.evaluation_recommend DESC
LIMIT 3

