mapper.select:
select segmentation y, count x from ( select ps.segmentation_name segmentation , count(distinct p.short_name,pfl.stage) count from project_segmentation ps left join project_financing_log pfl on ps.project_id = pfl.project_id left join projects p on ps.project_id = p.id left join investment_institutions ii on p.investment_institutions_id = ii.id where pfl.financing_time between #{beginTime} and #{endTime} and pfl.stage in ('种子轮','天使轮','Pre-A轮') and p.country = '中国'
and ii.type = #{jgtype}
group by ps.segmentation_name ) a order by a.count desc limit #{froma},#{sizea}

select city y, count x from (
select p.city, count(distinct p.short_name,pfl.stage) count
from project_financing_log pfl
left join projects p on pfl.project_id = p.id
left join investment_institutions ii on p.investment_institutions_id = ii.id
where pfl.financing_time between #{beginTime}
and #{endTime}
and pfl.stage in ('种子轮','天使轮','Pre-A轮') and p.country = '中国'
and p.city is not null

    and ii.type = #{jgtype}


group by p.city ) a
order by a.count desc limit #{froma},#{sizea}

select name y,count x from (select fe.education_experience name, fe.education_experience `value`, count(distinct mf.serial_number) count from project_financing_log pfl LEFT JOIN projects mf on pfl.project_id = mf.id LEFT JOIN founders f on mf.id = f.project_id left join investment_institutions_project iip on pfl.id = iip.project_id left join investment_institutions ii on iip.investment_institutions_id = ii.id right join founders_education fe on fe.founder_id = f.id and fe.education_experience != '' and fe.education_experience is not null where pfl.financing_time between #{beginTime} and #{endTime}
and ii.type = #{jgtype}
and pfl.stage in('种子轮','天使轮','Pre-A轮') and mf.country = '中国' group by fe.education_experience order by count desc limit #{froma},#{sizea} )a

select work_experience y,count x from (select fw.work_experience work_experience, count(distinct mf.serial_number) count from project_financing_log pfl LEFT JOIN projects mf on pfl.project_id = mf.id LEFT JOIN founders f on mf.id = f.project_id left join investment_institutions_project iip on pfl.id = iip.project_id left join investment_institutions ii on iip.investment_institutions_id = ii.id right join founders_work fw on fw.founder_id = f.id and fw.work_experience != '' and fw.work_experience is not null where pfl.financing_time between #{beginTime} and #{endTime}
and ii.type = #{jgtype}
and pfl.stage in('种子轮','天使轮','Pre-A轮') and mf.country = '中国' group by fw.work_experience order by count desc limit #{froma},#{sizea} )a

select city y, count x from (
select p.city, count(distinct p.full_name ) count
from project_financing_log pfl
left join projects p on pfl.project_id = p.id
left join investment_institutions ii on p.investment_institutions_id = ii.id
where pfl.financing_time between #{beginTime}
and #{endTime}
and pfl.stage in ('种子轮','天使轮','Pre-A轮') and p.country = '中国'
and p.city is not null

    and ii.type = #{jgtype}


group by p.city ) a
order by a.count desc limit #{froma},#{sizea}
