mapper.select:
select * from user_choose_record
where 1=1

 and user_id = #{userId}


  and scene_key= #{sceneKey}

order by create_time desc
limit 0,3

select ucr.id,u.ID as uid,ucr.create_time,ucr.action_type,ucr.log_type,u.company_name, u.company_duties,u.phonenumber,u.identity_type,ms.`desc`,acl.contact_status, u.actual_name from (select * from user_choose_record where 1=1
and ucr.create_time between #{beginTime} and #{endTime}
and user_id != 481 order by create_time desc ) ucr left join (select us.id,us.company_name,us.company_duties,us.phonenumber,us.identity_type, case when us.actual_name is null then (case when uw.nick_name is null then "" else uw.nick_name end) else us.actual_name end actual_name from users_weixin uw left join users us on uw.user_id = us.ID) u on ucr.user_id = u.ID left join meta_scene ms on ucr.scene_key = ms.`key` left join admin_contact_log acl on ucr.id = acl.user_choose_record_id where 1=1
and (u.actual_name like CONCAT(CONCAT('%', #{searchWord}), '%') or u.company_duties like CONCAT(CONCAT('%', #{searchWord}), '%') or u.company_name like CONCAT(CONCAT('%', #{searchWord}), '%') or ms.`desc` like CONCAT(CONCAT('%', #{searchWord}), '%') )
and ucr.action_type in #{item}
and acl.contact_status = 1
and acl.contact_status is null
limit #{startPage},#{pageSize}

select count(*) from ( select ucr.id,u.ID as uid,ucr.create_time,ucr.action_type,ucr.log_type,u.company_name, u.company_duties,u.phonenumber,u.identity_type,ms.`desc`,acl.contact_status, u.actual_name from (select * from user_choose_record where 1=1
and ucr.create_time between #{beginTime} and #{endTime}
and user_id != 481 order by create_time desc ) ucr left join (select us.id,us.company_name,us.company_duties,us.phonenumber,us.identity_type, case when us.actual_name is null then (case when uw.nick_name is null then "" else uw.nick_name end) else us.actual_name end actual_name from users_weixin uw left join users us on uw.user_id = us.ID) u on ucr.user_id = u.ID left join meta_scene ms on ucr.scene_key = ms.`key` left join admin_contact_log acl on ucr.id = acl.user_choose_record_id where 1=1
and (u.actual_name like CONCAT(CONCAT('%', #{searchWord}), '%') or u.company_duties like CONCAT(CONCAT('%', #{searchWord}), '%') or u.company_name like CONCAT(CONCAT('%', #{searchWord}), '%') or ms.`desc` like CONCAT(CONCAT('%', #{searchWord}), '%') )
and ucr.action_type in #{item}
and acl.contact_status = 1
and acl.contact_status is null
) a
