mapper.select:
select * from user_choose_record
where 1=1

 and user_id = #{userId}


  and scene_key= #{sceneKey}

order by create_time desc
limit 0,3

select * from ( select ucr.id,u.ID as uid,ucr.create_time,ucr.action_type,ucr.log_type,u.actual_name,u.company_name,u.company_duties,u.phonenumber,u.identity_type,ms.`desc`,acl.contact_status from user_choose_record ucr left join users u on ucr.user_id = u.ID left join meta_scene ms on ucr.scene_key = ms.`key` left join admin_contact_log acl on ucr.id = acl.user_choose_record_id where ucr.log_type = 1
and date(ucr.create_time)between(#{beginTime})and(#{endTime})
and ucr.action_type = #{actonType}
and acl.contact_status = #{contactStatus}
and u.actual_name like CONCAT(CONCAT('%', #{searchWord}), '%')
limit #{startPage},#{pageSize}
union
select ucr.id,u.ID as uid,ucr.create_time,ucr.action_type,ucr.log_type,u.actual_name,u.company_name,u.company_duties,u.phonenumber,u.identity_type,ms.`desc`,acl.contact_status from user_choose_record ucr left join users u on ucr.user_id = u.ID left join meta_scene ms on ucr.scene_key = ms.`key` left join admin_contact_log acl on ucr.id = acl.user_choose_record_id where ucr.log_type = 1
and date(ucr.create_time)between(#{beginTime})and(#{endTime})
and ucr.action_type = #{actonType}
and acl.contact_status = #{contactStatus}
and u.company_duties like CONCAT(CONCAT('%', #{searchWord}), '%')
limit #{startPage},#{pageSize}
union
select ucr.id,u.ID as uid,ucr.create_time,ucr.action_type,ucr.log_type,u.actual_name,u.company_name,u.company_duties,u.phonenumber,u.identity_type,ms.`desc`,acl.contact_status from user_choose_record ucr left join users u on ucr.user_id = u.ID left join meta_scene ms on ucr.scene_key = ms.`key` left join admin_contact_log acl on ucr.id = acl.user_choose_record_id where ucr.log_type = 1
and date(ucr.create_time)between(#{beginTime})and(#{endTime})
and ucr.action_type = #{actonType}
and acl.contact_status = #{contactStatus}
and u.company_name like CONCAT(CONCAT('%', #{searchWord}), '%')
limit #{startPage},#{pageSize}
union
select ucr.id,u.ID as uid,ucr.create_time,ucr.action_type,ucr.log_type,u.actual_name,u.company_name,u.company_duties,u.phonenumber,u.identity_type,ms.`desc`,acl.contact_status from user_choose_record ucr left join users u on ucr.user_id = u.ID left join meta_scene ms on ucr.scene_key = ms.`key` left join admin_contact_log acl on ucr.id = acl.user_choose_record_id where ucr.log_type = 1
and date(ucr.create_time)between(#{beginTime})and(#{endTime})
and ucr.action_type = #{actonType}
and acl.contact_status = #{contactStatus}
and ms.`desc` like CONCAT(CONCAT('%', #{searchWord}), '%')
limit #{startPage},#{pageSize}
) a order by a.create_time desc

