mapper.select:
select aa.id, case when aa.actual_name is not null then aa.actual_name else (case when aa.nick_name is not null then aa.nick_name else "" end) end realnaem, aa.company_name,aa.company_duties,aa.phonenumber,mit.type_name as identity_type, i.investors_type,aa.create_time,cc.end_time,mul.`name`,aa.update_time from (SELECT u.id,u.actual_name,uw.nick_name,u.company_name,u.company_duties,u.phonenumber,u.identity_type,u.create_time,u.update_time FROM users u right join users_weixin uw on u.id = uw.user_id where (u.actual_name is not null or uw.nick_name is not null ) and 1=1
and (u.actual_name like CONCAT("%",CONCAT(#{searchWord}),"%") or u.company_name like CONCAT("%",CONCAT(#{searchWord}),"%") or u.company_duties like CONCAT("%",CONCAT(#{searchWord}),"%") or u.phonenumber like CONCAT("%",CONCAT(#{searchWord}),"%"))
and (u.identity_type+1) = #{identityType}
and u.create_time between #{begainTime} and #{endTime}
) aa left join investors i on aa.id = i.user_id left join (select * from (select * from user_level_relation order by end_time desc) cc where cc.user_id is not null and cc.yn = 1 group by cc.user_id ) cc on aa.id = cc.user_id left join meta_user_level mul on cc.level_id = mul.id left join meta_identity_type mit on (aa.identity_type+1) = mit.id where aa.id is not null
and i.investors_type =#{investorType}
and cc.level_id = #{userLevelType}
group by aa.id
order by aa.create_time
desc
order by aa.update_time
desc
order by cc.end_time
desc
limit #{startPage},#{pageSize}

select count(*) ct from ( select aa.id, case when aa.actual_name is not null then aa.actual_name else (case when aa.nick_name is not null then aa.nick_name else "" end) end realnaem, aa.company_name,aa.company_duties,aa.phonenumber,mit.type_name as identity_type, i.investors_type,aa.create_time,cc.end_time,mul.`name`,aa.update_time from (SELECT u.id,u.actual_name,uw.nick_name,u.company_name,u.company_duties,u.phonenumber,u.identity_type,u.create_time,u.update_time FROM users u right join users_weixin uw on u.id = uw.user_id where (u.actual_name is not null or uw.nick_name is not null ) and 1=1
and (u.actual_name like CONCAT("%",CONCAT(#{searchWord}),"%") or u.company_name like CONCAT("%",CONCAT(#{searchWord}),"%") or u.company_duties like CONCAT("%",CONCAT(#{searchWord}),"%") or u.phonenumber like CONCAT("%",CONCAT(#{searchWord}),"%"))
and u.identity_type = #{identityType}
and u.create_time between #{begainTime} and #{endTime}
) aa left join investors i on aa.id = i.user_id left join (select * from (select * from user_level_relation order by end_time desc) cc where cc.user_id is not null and cc.yn = 1 group by cc.user_id ) cc on aa.id = cc.user_id left join meta_user_level mul on cc.level_id = mul.id left join meta_identity_type mit on (aa.identity_type+1) = mit.id where aa.id is not null
and i.investors_type =#{investorType}
and cc.level_id = #{userLevelType}
group by aa.id
order by aa.create_time
desc
order by aa.update_time
desc
order by cc.end_time
desc
) abc

SELECT u.id,u.company_name,u.company_duties,u.phonenumber, case when u.actual_name is null then uw.nick_name else u.actual_name end user_name FROM users u right join users_weixin uw on u.id = uw.user_id where 1=1 and (u.actual_name is not null or uw.nick_name is not null)
and (u.actual_name like CONCAT("%",CONCAT(#{searchWord},"%")) or uw.nick_name like CONCAT("%",CONCAT(#{searchWord},"%")) or u.company_name like CONCAT("%",CONCAT(#{searchWord},"%")) or u.company_duties like CONCAT("%",CONCAT(#{searchWord},"%")))
limit #{startPage},#{pageSize}
